private CoreContainer getCoreContainer(SolrQueryRequest req, SolrCore core) {
  CoreContainer coreContainer;
  if (core != null) {
     coreContainer = req.getCore().getCoreDescriptor().getCoreContainer();
  } else {
    coreContainer = cc;
  }
  return coreContainer;
}

