  new RectIntersectionTestHelper<Geo3dShape>(ctx) {

    @Override
    protected Geo3dShape generateRandomShape(Point nearP) {
      final Point centerPoint = randomPoint();
      final int maxDistance = random().nextInt(160) + 20;
