public void writeArray(String name, Collection val) throws IOException {
  writer.write('[');
  int sz = val.size();
  incLevel();
  boolean first=true;
  for (Object o : val) {
    if (first) {
      first=false;
    } else {
      writer.write(',');
    }
    if (sz>0) indent();
    writeVal(null, o);
  }
  decLevel();
  writer.write(']');
}

