public void writeArray(String name, Iterator val) throws IOException {
  writer.write('[');
  incLevel();
  boolean first=true;
  while( val.hasNext() ) {
    if( !first ) indent();
    writeVal(null, val.next());
    if( val.hasNext() ) {
      writer.write(',');
    }
    first=false;
  }
  decLevel();
  writer.write(']');
}

