public void testTokenizerPositions() throws Exception {
  EdgeNGramTokenizer tokenizer = new EdgeNGramTokenizer(new StringReader("abcde"), EdgeNGramTokenizer.Side.FRONT, 1, 3);
  assertTokenStreamContents(tokenizer,
                            new String[]{"a","ab","abc"},
                            new int[]{0,0,0},
                            new int[]{1,2,3},
                            null,
                            new int[]{1,0,0},
                            null,
                            null,
                            false);
}

