public double randomLon(boolean small) {
  double result;
  if (small) {
    result = GeoUtils.normalizeLon(originLon + lonRange * (random().nextDouble() - 0.5));
  } else {
    result = -180 + 360.0 * random().nextDouble();
  }
  return unscaleLon(scaleLon(result));
}

