  SimpleTextUtil.writeNewline(output);

  SimpleTextUtil.write(output, SI_USECOMPOUND);
  SimpleTextUtil.write(output, Boolean.toString(si.getUseCompoundFile()), scratch);
  SimpleTextUtil.writeNewline(output);

