public Reader charStream(Reader reader){
  if( charFilters != null && charFilters.length > 0 ){
    CharStream cs = new CharReader( reader );
    for (int i=0; i<charFilters.length; i++) {
      cs = charFilters[i].create(cs);
    }
    reader = cs;
  }
  return reader;
}

