@Override
public Name getPermissionName(AuthorizationContext ctx) {
  switch (ctx.getHttpMethod()) {
    case "GET":
      return Name.CONFIG_READ_PERM;
    case "POST":
      return Name.CONFIG_EDIT_PERM;
    default:
      return null;
  }
}

