private void appendFilters(ArrayList filters, String occurString, StringBuilder buffer)
{
  if (filters != null) {
    for (int i = 0; i < filters.size(); i++) {
      buffer.append(' ');
      buffer.append(occurString);
      buffer.append(filters.get(i).toString());
    }
  }
}

