@Override
public void removeFromParent() {
  if (this.parent != null) {
    List<QueryNode> parentChildren = this.parent.getChildren();
    Iterator<QueryNode> it = parentChildren.iterator();

    while (it.hasNext()) {
      if (it.next() == this) {
        it.remove();
      }
    }

    this.parent = null;
  }
}

