@Override
protected void ensureLog() {
  if (tlog == null) {
    String newLogName = String.format(Locale.ROOT, LOG_FILENAME_PATTERN,
        TLOG_NAME, id);
    HdfsTransactionLog ntlog = new HdfsTransactionLog(fs, new Path(tlogDir, newLogName),
        globalStrings);
    tlog = ntlog;

    if (tlog != ntlog) {
      ntlog.deleteOnClose = false;
      ntlog.decref();
      ntlog.forceClose();
    }
  }
}

