/** prints polygons as geojson */
public String toGeoJSON() {
  StringBuilder sb = new StringBuilder();
  sb.append("[");
  sb.append(Polygon.verticesToGeoJSON(y, x));
  for (XYPolygon hole : holes) {
    sb.append(",");
    sb.append(Polygon.verticesToGeoJSON(hole.y, hole.x));
  }
  sb.append("]");
  return sb.toString();
}

