static Query parseFieldQuery(QParser parser, Analyzer analyzer, String field, String queryText) {
  int phraseSlop = 0;
  boolean enablePositionIncrements = true;

  // most of the following code is taken from the Lucene QueryParser

  // Use the analyzer to get all the tokens, and then build a TermQuery,
  // PhraseQuery, or nothing based on the term count

  TokenStream source;
  try {
    source = analyzer.reusableTokenStream(field, new StringReader(queryText));
    source.reset();
  } catch (IOException e) {
    source = analyzer.tokenStream(field, new StringReader(queryText));
  }
  CachingTokenFilter buffer = new CachingTokenFilter(source);
  TermAttribute termAtt = null;
  PositionIncrementAttribute posIncrAtt = null;
  int numTokens = 0;

  boolean success = false;
  try {
    buffer.reset();
    success = true;
  } catch (IOException e) {
    // success==false if we hit an exception
  }
  if (success) {
    if (buffer.hasAttribute(TermAttribute.class)) {
      termAtt = (TermAttribute) buffer.getAttribute(TermAttribute.class);
    }
    if (buffer.hasAttribute(PositionIncrementAttribute.class)) {
      posIncrAtt = (PositionIncrementAttribute) buffer.getAttribute(PositionIncrementAttribute.class);
    }
  }

  int positionCount = 0;
  boolean severalTokensAtSamePosition = false;

  boolean hasMoreTokens = false;
  if (termAtt != null) {
    try {
      hasMoreTokens = buffer.incrementToken();
      while (hasMoreTokens) {
        numTokens++;
        int positionIncrement = (posIncrAtt != null) ? posIncrAtt.getPositionIncrement() : 1;
        if (positionIncrement != 0) {
          positionCount += positionIncrement;
        } else {
          severalTokensAtSamePosition = true;
        }
        hasMoreTokens = buffer.incrementToken();
      }
    } catch (IOException e) {
      // ignore
    }
  }
  try {
    // rewind the buffer stream
    buffer.reset();

    // close original stream - all tokens buffered
    source.close();
  }
  catch (IOException e) {
    // ignore
  }

  if (numTokens == 0)
    return null;
  else if (numTokens == 1) {
    String term = null;
    try {
      boolean hasNext = buffer.incrementToken();
      assert hasNext == true;
      term = termAtt.term();
    } catch (IOException e) {
      // safe to ignore, because we know the number of tokens
    }
    // return newTermQuery(new Term(field, term));
    return new TermQuery(new Term(field, term));
  } else {
    if (severalTokensAtSamePosition) {
      if (positionCount == 1) {
        // no phrase query:
        // BooleanQuery q = newBooleanQuery(true);
        BooleanQuery q = new BooleanQuery(true);
        for (int i = 0; i < numTokens; i++) {
          String term = null;
          try {
            boolean hasNext = buffer.incrementToken();
            assert hasNext == true;
            term = termAtt.term();
          } catch (IOException e) {
            // safe to ignore, because we know the number of tokens
          }

          // Query currentQuery = newTermQuery(new Term(field, term));
          Query currentQuery = new TermQuery(new Term(field, term));
          q.add(currentQuery, BooleanClause.Occur.SHOULD);
        }
        return q;
      }
      else {
        // phrase query:
        // MultiPhraseQuery mpq = newMultiPhraseQuery();
        MultiPhraseQuery mpq = new MultiPhraseQuery();
        mpq.setSlop(phraseSlop);
        List multiTerms = new ArrayList();
        int position = -1;
        for (int i = 0; i < numTokens; i++) {
          String term = null;
          int positionIncrement = 1;
          try {
            boolean hasNext = buffer.incrementToken();
            assert hasNext == true;
            term = termAtt.term();
            if (posIncrAtt != null) {
              positionIncrement = posIncrAtt.getPositionIncrement();
            }
          } catch (IOException e) {
            // safe to ignore, because we know the number of tokens
          }

          if (positionIncrement > 0 && multiTerms.size() > 0) {
            if (enablePositionIncrements) {
              mpq.add((Term[])multiTerms.toArray(new Term[0]),position);
            } else {
              mpq.add((Term[])multiTerms.toArray(new Term[0]));
            }
            multiTerms.clear();
          }
          position += positionIncrement;
          multiTerms.add(new Term(field, term));
        }
        if (enablePositionIncrements) {
          mpq.add((Term[])multiTerms.toArray(new Term[0]),position);
        } else {
          mpq.add((Term[])multiTerms.toArray(new Term[0]));
        }
        return mpq;
      }
    }
    else {
      // PhraseQuery pq = newPhraseQuery();
      PhraseQuery pq = new PhraseQuery();
      pq.setSlop(phraseSlop);
      int position = -1;


      for (int i = 0; i < numTokens; i++) {
        String term = null;
        int positionIncrement = 1;

        try {
          boolean hasNext = buffer.incrementToken();
          assert hasNext == true;
          term = termAtt.term();
          if (posIncrAtt != null) {
            positionIncrement = posIncrAtt.getPositionIncrement();
          }
        } catch (IOException e) {
          // safe to ignore, because we know the number of tokens
        }

        if (enablePositionIncrements) {
          position += positionIncrement;
          pq.add(new Term(field, term),position);
        } else {
          pq.add(new Term(field, term));
        }
      }
      return pq;
    }
  }

}

