/** Number of bytes to represent an unsigned int as a vint. */
private static int vIntSize(int x) {
  if ((x & (0xffffffff << (7*1))) == 0 ) {
    return 1;
  }
  if ((x & (0xffffffff << (7*2))) == 0 ) {
    return 2;
  }
  if ((x & (0xffffffff << (7*3))) == 0 ) {
    return 3;
  }
  if ((x & (0xffffffff << (7*4))) == 0 ) {
    return 4;
  }
  return 5;
}

