/** Flip the bit at the provided index. */
public void flip(int index) {
  assert index >= 0 && index < numBits: "index=" + index + " numBits=" + numBits;
  int wordNum = index >> 6;      // div 64
  int bit = index & 0x3f;     // mod 64
  long bitmask = 1L << bit;
  bits[wordNum] ^= bitmask;
}

