private void resolveVariables(Context ctx, Properties initProps) {
  for (Map.Entry<Object, Object> entry : initProps.entrySet()) {
    if (entry.getValue() != null) {
      entry.setValue(ctx.replaceTokens((String) entry.getValue()));
    }
  }
}

