    int numFiles = dir.listAll().length;

    // update again, number of files shouldn't change (old field's gen is
    // removed) 
    writer.updateBinaryDocValue(new Term("id", "d0"), f, toBytes(3L));
    writer.commit();

    assertEquals(numFiles, dir.listAll().length);
  }

  writer.close();
  dir.close();
}
