public void reconfigureHttpClient(HttpClientConfigurer configurer) {
  log.info("Reconfiguring the default client with: " + configurer);
  synchronized (client) {
    configurer.configure((DefaultHttpClient)client, getClientParams());
  }
}

public void reconfigureHttpClient(HttpClientConfigurer configurer) {
