public SegmentInfo(String name, int docCount, Directory dir, boolean isCompoundFile,
                   Codec codec) {
  this.name = name;
  this.docCount = docCount;
  this.dir = dir;
  delGen = NO;
  this.isCompoundFile = isCompoundFile;
