/** Sole constructor. */
protected BaseDirectory(LockFactory lockFactory) {
  super();
  if (lockFactory == null) {
    throw new NullPointerException("LockFactory cannot be null, use an explicit instance!");
  }
  this.lockFactory = lockFactory;
}

