 */
public boolean isWithin(final Vector point) {
  if (circlePlane == null)
    return false;
  if (!circlePlane.isWithin(point))
    return false;
  for (final Membership m : cutoffPlanes) {
    if (!m.isWithin(point)) {
      return false;
    }
  }
  return true;
}

