public UnInvertedField(String field, SolrIndexSearcher searcher) throws IOException {
  this.field = field;
  this.ti = new TermIndex(field,
          TrieField.getMainValuePrefix(searcher.getSchema().getFieldType(field)));
  uninvert(searcher);
}

