public void testDegeneratedTriangle() {
  double alat = 1e-26d;
  double alon = 0.0d;
  double blat = -1.0d;
  double blon = 0.0d;
  double clat = 1.0d;
  double clon = 0.0d;
  int alatEnc = GeoEncodingUtils.encodeLatitude(alat);
  int alonEnc = GeoEncodingUtils.encodeLongitude(alon);
  int blatEnc = GeoEncodingUtils.encodeLatitude(blat);
