private static Long randomValue(boolean allowNull) {
  if (valueRange == 0) {
    if (allowNull && random().nextInt(10) == 1) {
      return null;
    } else {
      return random().nextLong();
    }
  } else {
    return valueMid + TestUtil.nextInt(random(), -valueRange, valueRange);
  }
}

