@Override
public void finish(long sumTotalTermFreq) throws IOException {
  if (pendingCount > 0) {
    flushBlock();
  }
  // EOF marker:
  out.writeVInt(0);

  this.sumTotalTermFreq = sumTotalTermFreq;
  fieldIndexWriter.finish(out.getFilePointer());
}

