// NOTE: acceptDocs applies (and is checked) only in the
// parent document space
@Override
public Scorer scorer(LeafReaderContext readerContext, Bits acceptDocs, boolean needsScores) throws IOException {

  final Scorer childScorer = childWeight.scorer(readerContext, readerContext.reader().getLiveDocs(), needsScores);
  if (childScorer == null) {
    // No matches
    return null;
  }

  final int firstChildDoc = childScorer.nextDoc();
  if (firstChildDoc == DocIdSetIterator.NO_MORE_DOCS) {
    // No matches
    return null;
  }

  // NOTE: this does not take accept docs into account, the responsibility
  // to not match deleted docs is on the scorer
  final BitDocIdSet parents = parentsFilter.getDocIdSet(readerContext);

  if (parents == null) {
    // No matches
    return null;
  }

  return new BlockJoinScorer(this, childScorer, parents.bits(), firstChildDoc, scoreMode, acceptDocs);
}

