private static boolean isSentenceSeparator(char c) {
  // regex [!\\.\\?\\xA1\\xBF]
  switch (c) {
    case '!': return true;
    case '.': return true;
    case '?': return true;
    case 0xA1: return true; // spanish inverted exclamation mark
    case 0xBF: return true; // spanish inverted question mark
    default: return false;
  }   
}

