// LUCENE-3590: fix charsequence to fully obey interface
public void testCharSequenceCharAt() {
  CharsRef c = new CharsRef("abc");

  assertEquals('b', c.charAt(1));

  try {
    c.charAt(-1);
    fail();
  } catch (IndexOutOfBoundsException expected) {
    // expected exception
  }

  try {
    c.charAt(3);
    fail();
  } catch (IndexOutOfBoundsException expected) {
    // expected exception
  }
}

