// Returns true if an abort is in progress
synchronized boolean pauseAllThreads() {
  pauseThreads++;
  while(!allThreadsIdle()) {
    try {
      wait();
    } catch (InterruptedException ie) {
      throw new ThreadInterruptedException(ie);
    }
  }

  return aborting;
}

