protected Query pq( float boost, int slop, String field, String... texts ){
  PhraseQuery query = new PhraseQuery();
  for( String text : texts ){
    query.add( new Term( field, text ) );
  }
  query.setBoost( boost );
  query.setSlop( slop );
  return query;
}

