public boolean next() throws IOException {
  if (firstTime) {
    if (! exclScorer.next()) {
      exclScorer = null; // exhausted at start
    }
    firstTime = false;
  }
  if (reqScorer == null) {
    return false;
  }
  if (! reqScorer.next()) {
    reqScorer = null; // exhausted, nothing left
    return false;
  }
  if (exclScorer == null) {
    return true; // reqScorer.next() already returned true
  }
  return toNonExcluded();
}

