@Override
public MergeSpecification findMerges(MergeTrigger mergeTrigger, SegmentInfos segmentInfos)
    throws IOException {
  MergeSpecification ms = new MergeSpecification();
  if (doMerge) {
    OneMerge om = new OneMerge(segmentInfos.asList().subList(start, start + length));
    ms.add(om);
    doMerge = false;
    return ms;
  }
  return null;
}

