public RAMOutputStream(RAMFile f, boolean checksum) {
  file = f;

  // make sure that we switch to the
  // first needed buffer lazily
  currentBufferIndex = -1;
  currentBuffer = null;
  if (checksum) {
    crc = new BufferedChecksum(new CRC32());
  } else {
    crc = null;
  }
}

