private void checkBounds(int docID) {
  if (docID < 0 || docID >= maxDoc()) {       
    throw new IndexOutOfBoundsException("docID must be >= 0 and < maxDoc=" + maxDoc() + " (got docID=" + docID + ")");
  }
}

