@Override
public Analyzer getAnalyzer() {
  return new SolrAnalyzer() {

    @Override
    protected TokenStreamComponents createComponents(String fieldName,
        Reader reader) {
      try {
        return new TokenStreamComponents(new PreAnalyzedTokenizer(reader, parser));
      } catch (IOException e) {
        return null;
      }
    }

  };
}

