/*
 * Simple test for "commit on close": open writer, then
 * add a bunch of docs, making sure reader does not see
 * them until writer has closed.  Then instead of
 * closing the writer, call abort and verify reader sees
 * nothing was added.  Then verify we can open the index
 * and add docs to it.
 */
public void testCommitOnCloseAbort() throws IOException {
  MockRAMDirectory dir = new MockRAMDirectory();      
  IndexWriter writer = new IndexWriter(dir, new IndexWriterConfig(TEST_VERSION_CURRENT, new WhitespaceAnalyzer(TEST_VERSION_CURRENT)).setMaxBufferedDocs(10));
  for (int i = 0; i < 14; i++) {
    addDoc(writer);
  }
  writer.close();

  Term searchTerm = new Term("content", "aaa");        
  IndexSearcher searcher = new IndexSearcher(dir, false);
  ScoreDoc[] hits = searcher.search(new TermQuery(searchTerm), null, 1000).scoreDocs;
  assertEquals("first number of hits", 14, hits.length);
  searcher.close();

  writer = new IndexWriter(dir, new IndexWriterConfig(TEST_VERSION_CURRENT, new WhitespaceAnalyzer(TEST_VERSION_CURRENT))
    .setOpenMode(OpenMode.APPEND).setMaxBufferedDocs(10));
  for(int j=0;j<17;j++) {
    addDoc(writer);
  }
  // Delete all docs:
  writer.deleteDocuments(searchTerm);

  searcher = new IndexSearcher(dir, false);
  hits = searcher.search(new TermQuery(searchTerm), null, 1000).scoreDocs;
  assertEquals("reader incorrectly sees changes from writer", 14, hits.length);
  searcher.close();

  // Now, close the writer:
  writer.rollback();

  assertNoUnreferencedFiles(dir, "unreferenced files remain after rollback()");

  searcher = new IndexSearcher(dir, false);
  hits = searcher.search(new TermQuery(searchTerm), null, 1000).scoreDocs;
  assertEquals("saw changes after writer.abort", 14, hits.length);
  searcher.close();

  // Now make sure we can re-open the index, add docs,
  // and all is good:
  writer = new IndexWriter(dir, new IndexWriterConfig(TEST_VERSION_CURRENT, new WhitespaceAnalyzer(TEST_VERSION_CURRENT))
    .setOpenMode(OpenMode.APPEND).setMaxBufferedDocs(10));

  // On abort, writer in fact may write to the same
  // segments_N file:
  dir.setPreventDoubleWrite(false);

  for(int i=0;i<12;i++) {
    for(int j=0;j<17;j++) {
      addDoc(writer);
    }
    searcher = new IndexSearcher(dir, false);
    hits = searcher.search(new TermQuery(searchTerm), null, 1000).scoreDocs;
    assertEquals("reader incorrectly sees changes from writer", 14, hits.length);
    searcher.close();
  }

  writer.close();
  searcher = new IndexSearcher(dir, false);
  hits = searcher.search(new TermQuery(searchTerm), null, 1000).scoreDocs;
  assertEquals("didn't see changes after close", 218, hits.length);
  searcher.close();

  dir.close();
}

