private void printSegment(PrintWriter out, String segment, int docCount)
throws Exception {
   Directory directory = FSDirectory.getDirectory(indexDir, false);
   SegmentReader reader =
     SegmentReader.get(new SegmentInfo(segment, docCount, directory));

   for (int i = 0; i < reader.numDocs(); i++)
     out.println(reader.document(i));

   TermEnum tis = reader.terms();
   while (tis.next()) {
     out.print(tis.term());
     out.println(" DF=" + tis.docFreq());

     TermPositions positions = reader.termPositions(tis.term());
     try {
       while (positions.next()) {
         out.print(" doc=" + positions.doc());
         out.print(" TF=" + positions.freq());
         out.print(" pos=");
         out.print(positions.nextPosition());
         for (int j = 1; j < positions.freq(); j++)
           out.print("," + positions.nextPosition());
         out.println("");
       }
     } finally {
       positions.close();
     }
   }
   tis.close();
   reader.close();
   directory.close();
 }

