protected SolrServer getClient(String nodeName) {
  for (CloudJettyRunner cjetty : cloudJettys) {
    CloudSolrServerClient client = cjetty.client;
    if (client.shardName.equals(nodeName)) {
      return client.solrClient;
    }
  }
  return null;
}

