	int index = s.length() - 1;
	if ( index >= _R1   &&
		 s.endsWith("e") &&
		 !isVowel(sb.charAt(index-1)))
	{
		sb.delete(index, index + 1);
		unDouble(sb);
		_removedE = true;
	}
}

