public void testStop() throws Exception {
  Analyzer a = new StopAnalyzer();
  assertAnalyzesTo(a, "foo bar FOO BAR", 
                   new String[] { "foo", "bar", "foo", "bar" });
  assertAnalyzesTo(a, "foo a bar such FOO THESE BAR", 
                   new String[] { "foo", "bar", "foo", "bar" });
}

