commits.sourceNew:
// for debugging
String brToString(BytesRef b) {
  if (b == null) {
    return "null";
  } else {
    try {
      return b.utf8ToString() + " " + b;
    } catch (Throwable t) {
      // If BytesRef isn't actually UTF8, or it's eg a
      // prefix of UTF8 that ends mid-unicode-char, we
      // fallback to hex:
      return b.toString();
    }
  }
}

