private void findScorers(Scorer scorer) {
  Integer index = weightToIndex.get(scorer.getWeight());
  if (index != null) {
    if (index.intValue() == -1) {
      mainScorer = scorer;
    } else {
      subScorers[index] = scorer;
    }
  }
  for(ChildScorer child : scorer.getChildren()) {
    findScorers(child.child);
  }
}

