// for testing only
boolean normsClosed() {
  if (singleNormStream != null) {
    return false;
  }
  Iterator it = norms.values().iterator();
  while (it.hasNext()) {
    Norm norm = (Norm) it.next();
    if (norm.refCount > 0) {
      return false;
    }
  }
  return true;
}

// for testing only
boolean normsClosed() {
  if (singleNormStream != null) {
    return false;
  }
  for (final Norm norm : norms.values()) {
    if (norm.refCount > 0) {
      return false;
    }
  }
  return true;
}

