@Override
public void read(final DataInput indexIn, final boolean absolute) throws IOException {
  if (absolute) {
    upto = indexIn.readVInt();
    fp = indexIn.readVLong();
  } else {
    final int uptoDelta = indexIn.readVInt();
    if ((uptoDelta & 1) == 1) {
      // same block
      upto += uptoDelta >>> 1;
    } else {
      // new block
      upto = uptoDelta >>> 1;
      fp += indexIn.readVLong();
    }
  }
  assert upto < blockSize;
}

