          assert infos.info(i).info.dir == newReaders[i].getSegmentInfo().info.dir;
          assert infos.info(i).hasDeletions();
          newReaders[i] = new SegmentReader(infos.info(i), newReaders[i].core, IOContext.READ);
        }
      }
      success = true;
    } catch (IOException ex) {
      prior = ex;
