    Metric m = metricMap.get(name);
    if(m.outputLong) {
      Number num = (Number) val;
      map.put(name, Math.round(num.doubleValue()));
    } else {
      map.put(name, val);
    }
  } else {
    map.put(stat+"("+field+")", val);
  }
}

