public synchronized void setIndexFieldName(String dimName, String indexFieldName) {
  DimConfig ft = fieldTypes.get(dimName);
  if (ft == null) {
    ft = new DimConfig();
    fieldTypes.put(dimName, ft);
  }
  ft.indexFieldName = indexFieldName;
}

