/**
 * Returns total size in bytes of all of files used by this segment
 */
public long sizeInBytes() throws IOException {
    long sum = 0;
    for (final String fileName : files()) {
      sum += dir.fileLength(fileName);
    }
    sizeInBytes = sum;
    return sizeInBytes;
}

