  SolrCoreXMLDef coreDef = new SolrCoreXMLDef();
  Map<String,String> coreAttribs = new HashMap<String,String>();
  coreAttribs.put(instanceDirKey, instanceDirVal);
  coreDef.coreAttribs = coreAttribs ;
